import { styled } from "styled-components";

export const StyledExperienceSection = styled.div`
    width: 100vw;
    height: 150vh;
    background-color: #e9edf2;
    background-image: linear-gradient(top, #e9edf2 0%, #DBE4F0 100%);
    padding: 6% 15% 8%;
`;

export const StyledExperienceTimeline = styled.div`
    width: 100%;
    height: 100%;
    padding-top: 2rem;

    .experienceCard{
        background-color: transparent;
        border: none;
    }

    .outer{
        border-left: 2px solid var(--background-dark);
    }

    .experienceTitle::before {
        content: "";
        position: absolute;
        width: 10px;
        height: 10px;
        background: white;
        border-radius: 999px;
        left: -7px;
        top: 30px;
        border: 3px solid var(--background-dark);
    }
`;
