import { StyledAbout, StyledHobby } from "../styles/About.styled";

import PaletteIcon from '@mui/icons-material/Palette';
import MusicNoteIcon from '@mui/icons-material/MusicNote';
import SportsEsportsIcon from '@mui/icons-material/SportsEsports';
import PhotoCameraIcon from '@mui/icons-material/PhotoCamera';

export default function About() {
    return (
        <StyledAbout>
            <h1>About</h1>
            <div className="d-flex justify-content-between">
                <div className="col-5 d-flex flex align-items-center" style={{ backgroundColor: "crimson" }}>
                    <p>My name is Melissa Deng. I am software engineering student at the University of Waterloo by day and an artist at night.
                    </p>
                    <br />
                    <p>Passionate for imagination and problem-solving, I thrive to deliver smooth web experience to users. My ultimate goal is to bridge the gap between creativity and functionality, turning ideas into realities through my coding skills.</p>
                </div>
                <div className="col-5 d-flex align-items-center" style={{ backgroundColor: "blue", position: "relative" }}>
                    <div id="profilepic"></div>
                    <StyledHobby id="hobbyArt"><PaletteIcon sx={{ fontSize: "35px" }} style={{ color: "FFFFFF" }} /></StyledHobby>
                    <StyledHobby id="hobbyMusic"><MusicNoteIcon sx={{ fontSize: "35px" }} style={{ color: "FFFFFF" }} /></StyledHobby>
                    <StyledHobby id="hobbyGame"><SportsEsportsIcon sx={{ fontSize: "35px" }} style={{ color: "FFFFFF" }} /></StyledHobby>
                    <StyledHobby id="hobbyPhoto"><PhotoCameraIcon sx={{ fontSize: "35px" }} style={{ color: "FFFFFF" }} /></StyledHobby>

                </div>
            </div>
        </StyledAbout>
    )
}
